#[derive(Clone, Copy)]
pub struct SpriteFrame<const COUNT: usize> {
    pub width: usize,
    pub height: usize,
    pub img: [u8; COUNT],
}

pub struct Sprite<const COUNT: usize, const N_FRAMES: usize> {
    pub frames: [SpriteFrame<COUNT>; N_FRAMES]
}




pub fn get_sprite_from_spritesheet<const COUNT: usize, const N_FRAMES: usize>(
    frame_indecies: [usize; N_FRAMES],
    start_x: usize,
    start_y: usize,
    width: usize,
    height: usize
) ->   Sprite<COUNT, N_FRAMES> {

    let realsy = start_y * 8;
    let realsx = start_x * 8;
    let realw = width *8;
    let realh = height * 8;

    let sf1 = SpriteFrame::<COUNT> {
        width: realw,
        height: realh,
        img: [0b11111111; COUNT]
    };

    let mut frames: [SpriteFrame<COUNT>; N_FRAMES] = [sf1; N_FRAMES];

    for sprite_frame_i in 0..N_FRAMES {
            let ss_frame_index = frame_indecies[sprite_frame_i];
            // for each frame, index to that page,
            // (add 64 ** 2), get it row at a time.
            // for each row, put the pixels in.
            // then to jump to the next row, add the num_pages * page_row_len
            let mut sf = SpriteFrame::<COUNT>{
                width: realw,
                height: realh,
                img:[0b00000000; COUNT]
            };

            for row in 0..realh {

                // 2 bits pp means indecies are 4x smller
                let ss_row = row + realsy;
                let img_start = (row * realw) / 4;
                let ss_start = (ss_row * KITTY_SS_NUM_PAGES * KITTY_SS_PAGE_WIDTH + ss_frame_index * KITTY_SS_PAGE_WIDTH + realsx) / 4;
                sf.img[img_start..img_start + (realw / 4)].clone_from_slice(&KITTY_SS[ss_start..ss_start + (realw / 4)]);
            }
            
            frames[sprite_frame_i] = sf;
    }

    Sprite::<COUNT, N_FRAMES>{
        frames
    }
}

